DROP TABLE IF EXISTS FSTOOLS.CONVERTEDAIRPORTS;
DROP TABLE IF EXISTS FSTOOLS.RUNWAYS;
DROP TABLE IF EXISTS FSTOOLS.PARKINGSPOTS;
DROP TABLE IF EXISTS FSTOOLS.AIRPORTS;
DROP TABLE IF EXISTS FSTOOLS.INSTALLEDAIRCRAFT;
DROP TABLE IF EXISTS FSTOOLS.FLIGHTPLANAIRCRAFT;
CREATE TABLE IF NOT EXISTS FSTOOLS.CONVERTEDAIRPORTS(IDENT VARCHAR(4) NOT NULL,LATITUDE DECIMAL(10,8) NOT NULL,LONGITUDE DECIMAL(10,8) NOT NULL,ELEVATION INTEGER NOT NULL, PRIMARY KEY(IDENT));
CREATE TABLE IF NOT EXISTS FSTOOLS.AIRPORTS(IDENT VARCHAR(4) NOT NULL, LATITUDE DECIMAL(12,9) NOT NULL, LONGITUDE DECIMAL(12,9) NOT NULL, ALTITUDE DECIMAL(4,1) NOT NULL, AIRSPACE CHAR(1) NOT NULL, NUM_APPROACH_FREQS SMALLINT NOT NULL, TRAFFIC_SCALAR DECIMAL(3,2), COUNTRY VARCHAR(127), PRIMARY KEY(IDENT));
CREATE TABLE IF NOT EXISTS FSTOOLS.RUNWAYS(FK_IDENT VARCHAR(4) NOT NULL, LENGTH INTEGER NOT NULL, WIDTH INTEGER NOT NULL, SURFACE VARCHAR(5) NOT NULL, FOREIGN KEY(FK_IDENT) REFERENCES FSTOOLS.AIRPORTS(IDENT) ON DELETE CASCADE);
CREATE TABLE IF NOT EXISTS FSTOOLS.PARKINGSPOTS(FK_IDENT VARCHAR(4) NOT NULL, RADIUS DECIMAL(5,2) NOT NULL, PARKING_TYPE VARCHAR(10) NOT NULL, FOREIGN KEY(FK_IDENT) REFERENCES FSTOOLS.AIRPORTS(IDENT) ON DELETE CASCADE);
CREATE TABLE IF NOT EXISTS FSTOOLS.INSTALLEDAIRCRAFT(TITLE VARCHAR(128) NOT NULL,AIRCRAFT_FOLDER VARCHAR(512) NOT NULL,AIRLINE VARCHAR(64),ATC_TYPE VARCHAR(64),ATC_MODEL VARCHAR(32), UI_MANUFACTURER VARCHAR(128), UI_TYPE VARCHAR(64), UI_VARIATION VARCHAR(128), PRIMARY KEY(TITLE));
CREATE TABLE IF NOT EXISTS FSTOOLS.FLIGHTPLANAIRCRAFT(AC_ID INTEGER NOT NULL AUTO_INCREMENT,TITLE VARCHAR(128) NOT NULL,GROUNDSPEED INTEGER NOT NULL,AIRLINE VARCHAR(64),ATC_MODEL VARCHAR(32), PRIMARY KEY(AC_ID));
